CREATE DEFINER=`hazem10`@`%` PROCEDURE `create_reservation`(
IN p_guest_reservations_id INT,
IN p_room_number VARCHAR(255),
IN p_pax INT,
IN p_names LONGTEXT,
IN p_restaurant_id INT,
IN p_desired_date VARCHAR(255),
in p_time VARCHAR(255),
IN p_company_id INT,
IN p_hotel_id INT,
IN p_created_by LONGTEXT,
IN p_currencies_id INT,
IN p_price DECIMAL(10,3),
IN p_total_ammount DECIMAL(10,3),
IN p_exchange_rate DECIMAL(10,6),
IN p_paid TINYINT
)
back_door:BEGIN

DECLARE done INT DEFAULT 0;
DECLARE s_names LONGTEXT;
DECLARE s_pax INT;
DECLARE cur CURSOR FOR 
    SELECT names, pax 
    FROM reservations
    WHERE guest_reservations_id = p_guest_reservations_id 
    AND room_number = p_room_number
    AND day = p_desired_date
    AND time = p_time
    AND company_id = p_company_id
    AND hotel_id = p_hotel_id
    AND restaurant_id = p_restaurant_id;

DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;
OPEN cur;
read_loop: LOOP
    FETCH cur INTO s_names, s_pax;
    
    IF done THEN
        LEAVE read_loop;
    END IF;
    
    SET @counter = 1;

    WHILE @counter <= s_pax DO
        SET @current_name = TRIM(SUBSTRING_INDEX(SUBSTRING_INDEX(s_names, ' |-| ', @counter), ' |-| ', -1));
        IF p_names LIKE CONCAT('%', @current_name, '%') THEN
            SELECT 'alreadyReserved' as result;    
            LEAVE back_door;
        END IF;
        SET @counter = @counter + 1;
    END WHILE;
END LOOP;
CLOSE cur;


