CREATE DEFINER=`root`@`%` PROCEDURE `get_menu`(
IN p_language_code VARCHAR(45), 
IN p_company_id INT,
IN p_restaurant_pricing_times_id INT
)
BEGIN
    SELECT 
        mi.menus_items_id,
        it.name AS item_name,
        it.description AS item_description,
        mc_trans.name AS category_name,
        mc.menu_categories_id AS categories_id,
        ms_trans.name AS subcategory_name,
