          AND rpt.month = MONTH(current__date)
          AND rpt.day = DAY(current__date)
          AND rpt.menu_url IS NOT NULL;

        -- Move to the next day
        SET current__date = DATE_ADD(current__date, INTERVAL 1 DAY);
